import React from "react";

const Modal = ({ isOpen, onClose, client, onSave }) => {

    
  const [nom, setNom] = React.useState(client.nom);
  const [solde, setSolde] = React.useState(client.solde);
  const [obs, setObs] = React.useState(client.obs);

  if (!isOpen || !client) return null;
  
  const handleSubmit = () => {
    onSave({ ...client, nom, solde, obs });
    onClose();
  };


  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Modifier Client</h2>
        <label>Nom :</label>
        <input type="text" value={nom} onChange={(e) => setNom(e.target.value)} />

        <label>Solde :</label>
        <input type="number" value={solde} onChange={(e) => setSolde(e.target.value)} />

        <label>Observation :</label>
        <input type="text" value={obs} onChange={(e) => setObs(e.target.value)} />

        <button onClick={handleSubmit}>Enregistrer</button>
        <button onClick={onClose}>Annuler</button>
      </div>
    </div>
  );
};

export default Modal;
