import { useEffect , useState } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState(null);

  // Faire la requête vers le backend
  useEffect(() => {
      fetch('http://localhost:5000/bank')  // Remplacez par l'URL de votre serveur Express
          .then(response => response.json())
          .then(data => {
              setData(data);  // Sauvegarder les données dans l'état
          })
          .catch(error => console.error('Error fetching data:', error));
  }, []);

  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <div>
          <h1>Client Status</h1>
          <p>Total Solde: {data.totalSolde}</p>

          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>
            {data.clients.map(client => (
              <tr>
                <td key={client.numcompte}>{client.nom}</td>
                <td key={client.numcompte}>{client.solde}</td>
                <td key={client.numcompte}>{client.obs}</td>
                <td key={client.numcompte}>
                  <button></button>
                  <button></button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
          <ul>

          </ul>
      </div>
  );
}

export default App;
