import { useEffect , useState } from 'react';
import './css/App.css';
import EditModal from './EditClient';

function App() {
  const [data, setData] = useState(null);
  const [selectedClient, setSelectedClient] = useState(null);

  // Faire la requête vers le backend
  useEffect(() => {
      fetch('http://localhost:5000/bank')  // Remplacez par l'URL de votre serveur Express
          .then(response => response.json())
          .then(data => {
              setData(data);  // Sauvegarder les données dans l'état
          })
          .catch(error => console.error('Error fetching data:', error));
  }, []);

/*  const recupclient = (client) => {
    const clientData = {
      nom: client.nom,
      solde: client.solde,
      numcompte: client.numcompte
    };
    console.log('Client Data:', JSON.stringify(clientData)); // Affichage dans la console
    // Vous pouvez aussi effectuer une action supplémentaire ici (par exemple, envoyer les données à un autre composant ou à un serveur)
  };
*/
  const handleModify = (client) => {
    setSelectedClient(client);
  };

  const handleCloseModal = () => {
    setSelectedClient(null);
  };

  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <div>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>
            {data.clients.map(client => (
              <tr key={client.numcompte}>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                <button onClick={() => handleModify(client)}>Modifier</button>
                  <button>Supprimer</button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
          <EditModal client={selectedClient} onClose={handleCloseModal} />
      </div>
  );
}

export default App;
