import { useEffect , useState} from 'react';
import './css/App.css';
import { ModalAdd, ModalEdit } from './MainModel';
import fetchClients from './APIClient';
import * as React from 'react';
import { BarChart } from '@mui/x-charts/BarChart';


function ChartsOverviewDemo() {
  return (
    <BarChart
      series={[
        { data: [35, 44] },
        { data: [51, 6] },
      ]}
      height={500}
      xAxis={[{ data: ['Q1', 'Q2'], scaleType: 'band' }]}
      margin={{ top: 10, bottom: 30, left: 40, right: 10 }}
    />
  );
}

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  const handleClose = () => setIsOpen(false);
  const handleClick = () => setIsOpen(true);

  const handleEditClick = (client) => {
    setSelectedClient(client);
    setIsEditOpen(true);
  };

  const handleEditClose = () => {
    setIsEditOpen(false);
    setSelectedClient(null);
  };

  useEffect(() => {
    fetchClients().then((result) => {
      if (result) {
        setData(result);
        console.log(result);
      }
    });
  }, []);

  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <>
        <div>
          <ModalAdd isOpen={isOpen} handleClose={handleClose}/>
          <ModalEdit isOpen={isEditOpen} handleClose={handleEditClose} client={selectedClient}/>
        </div>
        <header>
          <h1>Liste des Clients</h1>
        </header>
        <main>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button onClick={handleClick}>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>              
              {data.clients.map(client => (
                <tr key={client.numcompte}>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                  <button onClick={() => handleEditClick(client)}>Modifier</button>
                  <button>Supprimer</button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
        </main>
        <footer>
            <ChartsOverviewDemo/>
        </footer>
      </>
  );
}

export default App;
