import { useEffect , useRef } from 'react';
import EditClient from './EditClient';

function ModalAdd({children, isOpen, handleClose}) {
    //  L'usage de cet ref va particulièrement nous intéresser 
    const dialogRef = useRef(null);
  
    const close = () => {
        dialogRef.current?.close();
    };
  
    useEffect(() => {
      const dialog = dialogRef.current;
      if (dialog) {
          if (isOpen && !dialog.open) {
              dialog.showModal();
          } else {
              dialog.close();
          }
      }
  }, [isOpen]);
  
    return (
        <dialog
            ref={dialogRef}
            //  capture l'élément 'close' et mise à jour de l'état du composant
            onClose={handleClose}
        >
            {children}
            <div>
                <button> Add </button>
                <button 
                    type="button" 
                    onClick={close} 
                >
                close
                </button>
            </div>
        </dialog>
    );
  }
  
function ModalEdit ({ isOpen, handleClose, client }) {
    const dialogRef = useRef(null);
  
    useEffect(() => {
      const dialog = dialogRef.current;
      if (dialog) {
        if (isOpen && !dialog.open) {
          dialog.showModal();
        } else {
          dialog.close();
        }
      }
    }, [isOpen]);
  
    return (
      <dialog ref={dialogRef} onClose={handleClose}>
        <h2>Modifier le client</h2>
        <EditClient client={client}/>
        <div>
            <button> Edit </button>
            <button type="button" onClick={handleClose}> Close </button>
        </div>
      </dialog>
    );
  }
  

export { ModalAdd , ModalEdit };
