import { useEffect , useRef , useState } from 'react';
import { BarChart } from '@mui/x-charts/BarChart';
import { Add } from './APIClient';

//  Structure du l'intérieur de la fenêtre Ajout et Modification
function CreateClient({ numcompte, setNumcompte, nom, setNom, solde, setSolde }) {
    return (
        <form>
            <div>
                <label>Numéro de compte :</label> 
                <input type='text' value={numcompte} onChange={(e) => setNumcompte(e.target.value)} />
            </div>
            <div>
                <label>Nom :</label> 
                <input type='text' value={nom} onChange={(e) => setNom(e.target.value)} />
            </div>
            <div>
                <label>Solde :</label> 
                <input type='text' value={solde} onChange={(e) => setSolde(e.target.value)} />
            </div>
        </form>
    );
}

function EditClient({ client }) {  
    return (
      <form>
        <div>
          <label>Numéro de compte : </label> 
          <input type='text' defaultValue={client?.numcompte} readOnly></input>    
        </div>
        <div>
          <label>Nom : </label> 
          <input type='text' placeholder={client?.nom}></input>
        </div>
        <div>
          <label>Solde : </label> 
          <input type='text' placeholder={client?.solde}></input>
        </div>
      </form>
    );
  }

//  Structure de la fenêtre ajout et modification

// Structure de la fenêtre d'ajout
function ModalAdd({ isOpen, handleClose }) {
    const dialogRef = useRef(null);
    const [numcompte, setNumcompte] = useState('');
    const [nom, setNom] = useState('');
    const [solde, setSolde] = useState('');

    const handleSubmit = async () => {
        if (!numcompte || !nom || !solde) {
            alert("Veuillez remplir tous les champs");
            return;
        }
        if( Add(numcompte, nom, solde) ){
          handleClose();
        }
    };

    useEffect(() => {
        const dialog = dialogRef.current;
        if (dialog) {
            if (isOpen && !dialog.open) {
                dialog.showModal();
            } else {
                dialog.close();
            }
        }
    }, [isOpen]);

    return (
        <dialog ref={dialogRef} onClose={handleClose}>
            <header>
                <h1>Ajouter un Client</h1> 
            </header>
            <main>
                <CreateClient 
                    numcompte={numcompte} setNumcompte={setNumcompte} 
                    nom={nom} setNom={setNom} 
                    solde={solde} setSolde={setSolde} 
                />
            </main>
            <footer>
                <button onClick={handleSubmit}>Add</button>
                <button type="button" onClick={handleClose}>Close</button>
            </footer>
        </dialog>
    );
}

  
function ModalEdit ({ isOpen, handleClose, client }) {
    const dialogRef = useRef(null);
  
    useEffect(() => {
      const dialog = dialogRef.current;
      if (dialog) {
        if (isOpen && !dialog.open) {
          dialog.showModal();
        } else {
          dialog.close();
        }
      }
    }, [isOpen]);
  
    return (
      <dialog ref={dialogRef} onClose={handleClose}>
        <header>
            <h2>Modifier le client</h2>
        </header>
        <main>
            <EditClient client={client}/>
        </main>
        <div>
            <button> Edit </button>
            <button type="button" onClick={handleClose}> Close </button>
        </div>
      </dialog>
    );
  }

  function ChartModel({ soldeMin, soldeMax, totalSolde }) {
  return (
    <BarChart
      series={[
        {
          data: [soldeMin, soldeMax, totalSolde], // Données pour les soldes
        },
      ]}
      height={500}
      xAxis={[{ data: ['Solde Min', 'Solde Max', 'Total Solde'], scaleType: 'band' }]}
      margin={{ top: 10, bottom: 30, left: 40, right: 10 }}
      color={['#0055A4', '#FFFFFF', '#EF4135']} // Bleu, Blanc, Rouge
    />
  );
}


export { ModalAdd , ModalEdit , ChartModel };
