import { useEffect , useRef } from 'react';

function ModalAdd({children, isOpen, handleClose}) {
    //  L'usage de cet ref va particulièrement nous intéresser 
    const dialogRef = useRef(null);
  
    const close = () => {
        dialogRef.current?.close();
    };
  
    useEffect(() => {
      const dialog = dialogRef.current;
      if (dialog) {
          if (isOpen && !dialog.open) {
              dialog.showModal();
          } else {
              dialog.close();
          }
      }
  }, [isOpen]);
  
    return (
        <dialog
            ref={dialogRef}
            //  capture l'élément 'close' et mise à jour de l'état du composant
            onClose={handleClose}
        >
            {children}
            <div>
                <button> edit </button>
                <button 
                    type="button" 
                    onClick={close} 
                >
                close
                </button>
            </div>
        </dialog>
    );
  }
  
  function ModalEdit ({ isOpen, handleClose, client }) {
    const dialogRef = useRef(null);
  
    useEffect(() => {
      const dialog = dialogRef.current;
      if (dialog) {
        if (isOpen && !dialog.open) {
          dialog.showModal();
        } else {
          dialog.close();
        }
      }
    }, [isOpen]);
  
    return (
      <dialog ref={dialogRef} onClose={handleClose}>
        <h2>Modifier le client</h2>
        <form>
          <label>
            Nom :
            <input type="text" placeholder={client?.nom || "Nom"} />
          </label>
          <label>
            Solde :
            <input type="text" placeholder={client?.solde || "Solde"} />
          </label>
          <label>
            Numéro de compte :
            <input type="text" placeholder={client?.numcompte || "Numéro de compte"} />
          </label>
        </form>
        <button type="button" onClick={handleClose}>
          Fermer
        </button>
      </dialog>
    );
  }
  

export { ModalAdd , ModalEdit };
