import { useEffect , useState} from 'react';
import './css/App.css';
import { ModalAdd, ModalEdit, DeleteClient ,ChartModel } from './MainModel';
import { fetchClients } from './APIClient';



function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  const handleClose = () => setIsOpen(false);
  const handleClick = () => setIsOpen(true);

  const handleEditClick = (client) => {
    setSelectedClient(client);
    setIsEditOpen(true);
  };

  const handleEditClose = () => {
    setIsEditOpen(false);
    setSelectedClient(null);
  };

  const refreshClients = async () => {
    const result = await fetchClients();
    if (result) {
      setData(result);
      console.log("Clients mis à jour :", result);
    }
  };
  useEffect(() => {
    refreshClients();
  }, []); 

  if (!data) {
      return <div>Loading...</div>;
  }
  if (data.undefinedValues){
    return(
      <div>
        <button onClick={handleClick}>Ajouter</button>
        <ModalAdd isOpen={isOpen} handleClose={handleClose} onRefresh={refreshClients}/>
      </div>
    ) 
  }

  const soldeMin = data.soldeInstance[0]?.solde_min || 0;
  const soldeMax = data.soldeInstance[0]?.solde_max || 0;
  const totalSolde = data.totalSolde || 0;

  return (
      <>
        <div>
          <ModalAdd isOpen={isOpen} handleClose={handleClose} onRefresh={refreshClients}/>
          <ModalEdit isOpen={isEditOpen} handleClose={handleEditClose} client={selectedClient} onRefresh={refreshClients}/>
        </div>
        <header>
          <h1>Liste des Clients</h1>
        </header>
        <main>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button onClick={handleClick}>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>              
              {data.clients.map(client => (
                <tr key={client.numcompte}>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                  <button onClick={() => handleEditClick(client)}>Modifier</button>
                  <DeleteClient numcompte={client?.numcompte} onRefresh={refreshClients} ></DeleteClient>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
        </main>
        <footer>
            <ChartModel soldeMin={soldeMin} soldeMax={soldeMax} totalSolde={totalSolde} />
        </footer>
      </>
  );
}

export default App;
