import { useEffect , useState } from 'react';
import './css/App.css';
import fetchClients from './APIClient';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetchClients().then((result) => {
      if (result) {
        setData(result);
      }
    });
  }, []);

    const recupClient = (client) => {
    const clientData = {
      nom: client.nom,
      solde: client.solde,
      numcompte: client.numcompte
    };
    console.log('Client Data:', JSON.stringify(clientData)); // Affichage dans la console
    // Vous pouvez aussi effectuer une action supplémentaire ici (par exemple, envoyer les données à un autre composant ou à un serveur)
  };


  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <>
        <header>
          <h1>Liste des Clients</h1>
        </header>
        <main>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>              
              {data.clients.map(client => (
                <tr key={client.numcompte}>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                  <button onClick={() => recupClient(client)}>Modifier</button>
                  <button>Supprimer</button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
        </main>
        <footer>

        </footer>
      </>
  );
}

export default App;
