import { useEffect , useState } from 'react';
import './css/App.css';

function App() {
  const [data, setData] = useState(null);

  // Faire la requête vers le backend
  useEffect(() => {
      fetch('http://localhost:5000/bank')  // Remplacez par l'URL de votre serveur Express
          .then(response => response.json())
          .then(data => {
            console.log("Données avant suppression des doublons:", data);

            // Filtrer les doublons en utilisant numcompte comme clé unique
            const uniqueClients = data.clients.filter((value, index, self) =>
              index === self.findIndex((t) => t.numcompte === value.numcompte)
            );

            console.log("Données après suppression des doublons:", uniqueClients);

            // Mettre à jour l'état avec les données uniques
            setData({ ...data, clients: uniqueClients });
          })
          .catch(error => console.error('Error fetching data:', error));
  }, []);

  /*
    const checkUniqueKeys = (clients) => {
    const keys = clients.map(client => client.numcompte);
    const uniqueKeys = new Set(keys);
    if (keys.length !== uniqueKeys.size) {
      console.warn('Duplicate numcompte values found:', keys);
    }
  };
  */

  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <div>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>
            {data.clients.map(client => (
              <tr key={client.numcompte}>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                <button>Modifier</button>
                  <button>Supprimer</button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
          <ul>

          </ul>
      </div>
  );
}

export default App;
