import { useEffect , useState } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState(null);

  // Faire la requête vers le backend
  useEffect(() => {
      fetch('http://localhost:5000/bank')  // Remplacez par l'URL de votre serveur Express
          .then(response => response.json())
          .then(data => {
              setData(data);  // Sauvegarder les données dans l'état
          })
          .catch(error => console.error('Error fetching data:', error));
  }, []);

  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <div>
          <h2>Clients:</h2>
          <ul>
              {data.results.map(result => (
                  <li key={result.numcompte}>{result.nom}: {result.solde}</li>
              ))}
          </ul>
      </div>
  );
}

export default App;
