import { useEffect , useState, useRef } from 'react';
import './css/App.css';
import fetchClients from './APIClient';


function Modal({children, isOpen, handleClose}) {
  // ðŸ‘‡ L'usage de cet ref va particuliÃ¨rement nous intÃ©resser 
  const dialogRef = useRef<HTMLDialogElement>(null);

  const close = () => {
      dialogRef.current?.close();
  };

  useEffect(() => {
    const dialog = dialogRef.current;
    if (dialog) {
        if (isOpen && !dialog.open) {
            dialog.showModal();
        } else {
            dialog.close();
        }
    }
}, [isOpen]);

  return (
      <dialog
          ref={dialogRef}
          // ðŸ‘‡ capture l'Ã©lÃ©ment 'close' et mise Ã  jour de l'Ã©tat du composant
          onClose={handleClose}
      >
          {children}

          <button 
              type="button" 
              onClick={close} 
              title="close modal" 
              aria-label="close modal"
              >
              close
          </button>
      </dialog>
  );
}

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const handleClose = (() => {
      setIsOpen(false);
  });

  const handleClick = (() => {
      setIsOpen(true);
  });

  useEffect(() => {
    fetchClients().then((result) => {
      if (result) {
        setData(result);
      }
    });
  }, []);

    const recupClient = (client) => {
    const clientData = {
      nom: client.nom,
      solde: client.solde,
      numcompte: client.numcompte
    };
    console.log('Client Data:', JSON.stringify(clientData)); // Affichage dans la console
    // Vous pouvez aussi effectuer une action supplÃ©mentaire ici (par exemple, envoyer les donnÃ©es Ã  un autre composant ou Ã  un serveur)
  };


  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <>
        <div>
        <Modal 
                isOpen={isOpen} 
                handleClose={handleClose}
            >
                {/* Le contenu de la modale */}
                <header>
                    <h1>Hello world</h1>
                    <small>pour Ãªtre original...</small>
                </header>
            </Modal>


            <button
                type="button"
                onClick={handleClick}
            >
                Open modal
            </button>
        </div>
        <header>
          <h1>Liste des Clients</h1>
        </header>
        <main>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>              
              {data.clients.map(client => (
                <tr key={client.numcompte}>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                  <button onClick={() => recupClient(client)}>Modifier</button>
                  <button>Supprimer</button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
        </main>
        <footer>

        </footer>
      </>
  );
}

export default App;
