import { useEffect , useState } from 'react';

export default function SubApp () {
const [data, setData] = useState(null);
// Faire la requête vers le backend
useEffect(() => {
      fetch('http://localhost:5000/bank')  // Remplacez par l'URL de votre serveur Express
          .then(response => response.json())
          .then(data => {
              setData(data);  // Sauvegarder les données dans l'état
          })
          .catch(error => console.error('Error fetching data:', error));
    }, []);
return (
    <table>
        <thead>
        <tr>
            <th>Nom</th>
            <th>Solde</th>
            <th>Observation</th>
            <th>
            <button>Ajouter</button>
            </th>
        </tr>
        </thead>
        <tbody>
        {data.clients.map(client => (
        <tr>
            <td>{client.nom}</td>
            <td>{client.solde}</td>
            <td>{client.obs}</td>
            <td>
            
            </td>
        </tr>      
        ))}
        </tbody>
    </table>
);
}
