import { useEffect , useState } from 'react';
import './css/App.css';
import Modal from './EditClient';

function App() {
  const [data, setData] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  // Faire la requête vers le backend
  useEffect(() => {
      fetch('http://localhost:5000/bank')  // Remplacez par l'URL de votre serveur Express
          .then(response => response.json())
          .then(data => {
              setData(data);  // Sauvegarder les données dans l'état
          })
          .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleEditClick = (client) => {
    setSelectedClient(client);
    setIsModalOpen(true);
  };

  const handleSave = (updatedClient) => {
    // Ici, envoie les modifications au backend
    fetch(`http://localhost:5000/bank/update/${updatedClient.numcompte}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedClient),
    })
      .then(() => {
        setData((prevData) => ({
          ...prevData,
          clients: prevData.clients.map((c) =>
            c.numcompte === updatedClient.numcompte ? updatedClient : c
          ),
        }));
      })
      .catch((error) => console.error("Error updating client:", error));
  };

  if (!data) {
      return <div>Loading...</div>;
  }

  return (
      <div>
          <table>
            <thead>
              <tr>
                <th>Nom</th>
                <th>Solde</th>
                <th>Observation</th>
                <th>
                  <button>Ajouter</button>
                </th>
              </tr>
            </thead>
            <tbody>
            {data.clients.map(client => (
              <tr>
                <td>{client.nom}</td>
                <td>{client.solde}</td>
                <td>{client.obs}</td>
                <td>
                  <button onClick={() => handleEditClick(client)}>Modifier</button>
                  <button>Supprimer</button>
                </td>
              </tr>      
            ))}
            </tbody>
          </table>
          <Modal
            isOpen={isModalOpen}
            onClose={() => setIsModalOpen(false)}
            client={selectedClient}
            onSave={handleSave}
          />
      </div>
  );
}

export default App;
